flowchart:
@startuml
start

:流程开始;
repeat :将零件接收至“隔离”区;
:调查故障原因;
fork
  :记录数据;
  stop
fork again
  :处理决定;
  if (外部返工?) then (是)
    :记录返工说明;
    :发送至“派送”;
    stop
  else (否)
    :记录返工说明;
    :执行返工流程;
    :返回至“最终检验”;
    stop
  endif
repeat while(需要重新隔离?) is(是) not (否)
end fork
@enduml

@startuml
start
:高考成绩公布;
:学生获取高考成绩单;
:学生查看高考成绩，确定填报志愿的院校和专业范围;
repeat
  if (需要提前调研院校专业?) then (是)
    :学生进行院校专业调研;
  else (否)
    :跳过此步骤;
  endif
  :学生登录填报志愿系统;
  :系统提供填报志愿的入口和说明;
  :学生填写志愿，按照优先级排序;
  :填报完成后系统生成志愿表;
  :学生确认无误后提交志愿表;
  if (填报截止?) then (是)
    :系统关闭填报入口;
    :等待录取结果公布;
  else (否)
    :学生可在截止前修改志愿;
    :等待填报截止;
  endif
repeat while(志愿填报错误?) is(是) not (否)
:录取结果公布;
if (被录取?) then (是)
  :学生按照录取通知书要求办理报到手续;
else (否)
  :学生进行补报志愿或参加征集志愿;
endif
:报到入学;
stop
@enduml

@startuml
start

:列出商品;
repeat
  :加入购物车;
  if (商品已加入?) then (是)
    :已加入购物车;
    :登录;
    :查看购物车;
      :存储信息;;
    break
  else (否)
  endif
repeat while (用户未完成购物) is(继续购物) not (完成购物)

stop
@enduml

@startuml 87c
start

repeat :扫描商品;
if (客户是公司还是个人?) then (个人)
  :处理付款;
  stop
else (公司)
  :登记公司信息;
  :处理付款;
  :打印发票;
  stop
endif
repeat while(还有更多商品?) is(有) not (没有)

@enduml

@startuml
start

:登录DK;
repeat
  if (搜索图书?) then (是)
    :搜索图书;
    if (找到图书?) then (是)
      :选择图书;
      :购买图书;
      break
    else (否)
     :继续搜索;
    endif
  else (否)
  endif
repeat while (再次搜索?) is(是) not (否)

stop
@enduml

@startuml
start

:查询;
repeat
  :检查可用性;
  if (可用?) then (不可用)
    :不可用;
    stop
  else (可用)
    :检查会员;
    if (会员状态?) then (无效)
      :不可用;
      stop
    else (有效)
      :已发放的图书总数;
      if (有未发放的图书?) then (不可用)
        :不可用;
      else (可用)
        :图书已发放;
      endif
    endif
  endif
repeat while(条件满足?) is(继续查询) not (停止查询)

stop
@enduml

@startuml
start
repeat
  :登录系统;

  if (还书?) then (yes)
    :收回书;
    if (是否过期?) then (yes)
      :收费;
    endif
    :修改信息;
  else (no)
    :借出书;
  endif

  :退出系统;
repeat while(有更多事务处理?) is(有) not (无)

stop
@enduml

@startuml
start

:通过电子邮件接收订单;
repeat
  :将电子邮件数据复制到数据库;
  if (涉及运输?) then (是)
    :打印发票和UPS标签;
    :发送确认运输的电子邮件;
    :包装并发货;
  else (否)
    :提供数字交付;
  endif
repeat while (更多订单?) is (是) not (否)

end
@enduml

@startuml
start

:流程开始;
:将零件接收至“隔离”区;
repeat :调查故障原因;
fork
  :记录数据;
  stop
fork again
  :处理决定;
  if (外部返工?) then (是)
    :记录返工说明;
    :发送至“派送”;
    stop
  else (否)
    :记录返工说明;
    :执行返工流程;
    :返回至“最终检验”;
    stop
  endif
repeat while(故障未解决?) is(未解决) not (已解决)
end fork
@enduml

@startuml 93c
start

repeat
  :接收账单;
  :录入数据;
  :核对账单;
  if (匹配?) then (是)
    :批准账单;
    break
  else (否)
    :标记并解决差异;
  endif
repeat while (存在差异?) is (是) not (否)

stop
@enduml

@startuml 94c
start

:客户访问商店网站;

repeat
  :产品详情页;
  if (加入购物车?) then (是)
    :结账页面;
    if (客户有账户?) then (是)
      :输入登录凭证;
    else (否)
      :输入客户信息;
    endif
    :支付网关;
    if (成功?) then (是)
      :订单确认;
      break
    endif
  endif
repeat while (未成功) is (是) not (否)

stop
@enduml

@startuml
start

:流程开始;
:接收零件;
repeat
  :记录数据;

  if (检验通过?) then (no)
    :退回供应商;
    stop
  else (yes)
    :转移至仓库;
    :记录数据;
    :发放至生产线;
    :注塑;
    :固化;

    if (最终检验通过?) then (no)
      :隔离;
      repeat while(!问题解决?) is (未解决)
    else (yes)
      :发货;
    split
      :记录数据;
    kill
    split again
      :流程结束;
    end split
    endif
  endif
repeat while(接收更多零件?) is (是)
stop
@enduml

@startuml 96c
start

repeat :下载试用版;
:选择购买版本;

if (通过信用卡还是采购订单支付?) then (采购订单)
  :提交采购订单;
  :接收发票;

else (信用卡)
  :点击结账按钮;
  :输入订单详情;

endif
  :接收确认邮件;
  :注册产品;
repeat while(满意试用情况?) is(不满意) not (满意)
  :完成;

stop
@enduml

@startuml 97c
start

:新员工填写文件;
repeat
  :文件发送至管理层审批;

  if (准确性检查?) then (文件通过审批)
    :HR为新员工准备工作空间;
    break
  else (文件修正请求)
    :新员工修正文件;
  endif
repeat while (文件未通过审批)

stop
@enduml

@startuml 98c
start

:关键词想法;
if (批准?) then (是)
  repeat
    :作者创建文章草稿 (包括图片想法);
    :编辑校对文章并提供反馈;
    if (需要修改?) then (是)
      :作者进行修改;
    else (否)
      :设计师创建图形;
    endif
  repeat while(修改完成?) is 否
  :营销人员优化文章并发布;
  :营销团队进行推广;
  stop

else (否)
  :关键词被拒绝;
  stop
endif
@enduml

@startuml 99c
start

:列出商品;
repeat
  :加入购物车;
  if (商品已加入?) then (是)
    :已加入购物车;
    :登录;
    :查看购物车;
    fork
      :存储信息;
    fork again
      :打开链接;
    end fork
    break
  else (否)
  endif
repeat while (继续添加商品?) is(是) not (否)

stop
@enduml

@startuml 
start

repeat
  :接收到采购订单;

  if (现有客户?) then (是)
    :将订单录入系统;
  endif
  else (否)
    if (客户来自美国?) then (是)
      :处理新客户记录;
      :录入订单;
    else (否)
      :提交给主管审批;
    endif
  endif

  :处理订单;

repeat while(还有未处理订单?) is(是) not (否)
stop
@enduml

@startuml
start
:选择合适的歌曲;
:安排合唱练习时间和地点;
:分发歌词和乐谱给学生;
:指导学生学习歌曲;
repeat
  :练习音阶和节奏;
  :练习合唱部分;
  if (需要调整声部？) then (是)
    :分配声部;
    :练习各声部独唱和合唱;
  else (否)
    :继续整体合唱练习;
  endif
  :指导学生注意和谐、节奏等;
  :练习完整歌曲;
repeat while(满意阶段进度?) is (未满意) not (已满意)
:安排排练表演;
:练习表演姿态和表情;
:进行完整排练;
if (需要调整？) then (是)
  :进行调整和再次排练;
else (否)
  :确认表演细节;
endif
:结束大合唱排练;
stop
@enduml

@startuml
start
:登录学校线上预约系统;
:选择琴房预约功能;
repeat
  :查看琴房预约情况和时间可用性;
  :选择预约日期和时间段;
  if (选择的时间段可用？) then (是)
    :填写预约目的和联系信息;
    :确认预约信息;
    :提交预约请求;
    if (需要支付费用？) then (是)
      :选择支付方式;
      :完成支付流程;
    else (否)
      :等待系统确认预约;
    endif
  else (否)
    :选择其他可用时间段;
  endif
repeat while(预约成功？) is(未成功) not (已成功)
:系统发送预约确认信息;
:按时前往琴房练习;
:结束琴房预约流程;
stop
@enduml

@startuml
start

:确定比赛日期、时间和地点;
:发布比赛通知和规则;
:筛选参赛选手;
:安排参赛选手的演奏顺序;
:比赛当天签到;
:参赛选手依次进行演奏;
repeat
    :主持人宣布姓名和表演曲目;
    :钢琴伴奏上场;
    :小提琴演奏者上场;
    :鞠躬;
    :开始演奏;
repeat while (所有选手演奏完毕？) is (否)
:评委评分;
:计算得分并排名;
:颁发奖项;
:结束比赛;
stop
@enduml

@startuml
start
:选择照明设计的场所类型 (室内/室外);
if (场所类型为室内?) then (是)
  :一般照明;
  :装饰照明;
  :重点照明;
  :任务照明;
  :调光需求;
else (否)
  :景观照明;
  :安全照明;
  :导向照明;
  :装饰照明;
  :节能需求;
endif
repeat
  :选择功能需求 (一般照明/装饰照明/重点照明);
  :考虑用户需求 (舒适性/节能等);
  if (需要节能设计?) then (是)
    :考虑节能要求和方案;
  else (否)
    :不考虑节能设计;
  endif
  :整理需求分析结果;
repeat while (设计要求未满足?) is (不满足) not(已满足)
stop
@enduml

@startuml
start
:确定照明需求和功能需求;
repeat
  if (需要传统照明?) then (是)
    :选择白炽灯;
    :根据需求确定瓦数和色温;
  else (否)
    if (需要荧光灯?) then (是)
      :选择荧光灯;
      :根据需求确定类型和色温;
    else
      :选择LED灯;
      :根据需求确定功率和色温;
    endif
  endif
  :根据需求选择光源亮度和色温;
  :设计灯具布局方案;
  if (需要光源调光?) then (是)
    :考虑可调光光源和调光设备;
  else (否)
    :不考虑光源调光;
  endif
  :评估灯具布局效果;
  :调整灯具位置和数量;
repeat while (照度要求未满足?) is (否) not (是)
:最终确定光源选择和灯具布局方案;
stop
@enduml

@startuml
start
:与施工队沟通照明设计方案;
:解释设计需求和灯具布局;
:提供技术支持和指导;
:协调安装时间和进度;
:监督灯具安装过程;
:进行灯具调试和亮度调整;
repeat
  :确认灯具布局和安装位置;
  :监督灯具安装过程;
    :检查灯具安装质量和安全性;
    if (需要调整灯具位置?) then (是)
      :协商调整方案;
      :重新安装或调整灯具位置;
    else (否)
      :确认最终安装结果;
    endif
repeat while(灯具调试成功?) is (未成功) not (成功)
:最终验收照明系统;
:确保照明系统符合设计要求;
:完善施工文档和记录;
stop
@enduml

@startuml
start
:制定验收标准和流程;
repeat
  :对比设计方案和实际实施情况;
  if (符合标准?) then (是)
    :完成照明系统验收;
    :记录验收结果;
    :移交给维护团队;
  else (否)
    :确定问题和不符合之处;
    :提出整改要求;
    :重新进行调整和验收;
  endif
repeat while(验收失败?) is(未通过) not (通过)
:建立定期维护计划;
:检查灯具运行情况和亮度;
:清洁灯具和灯具周围环境;
if (发现故障或异常?) then (是)
  :诊断问题原因;
  :修复或更换故障灯具;
  :记录维护信息;
else (否)
  :记录维护情况;
endif
:定期进行系统性能评估;
:根据评估结果调整维护计划;
:持续监督照明系统运行;
stop
@enduml

@startuml
start
:收集需求和功能规格;
:分析照明系统控制需求;
:确定控制策略和方案;
:选择合适的控制设备和技术;
if (需要远程控制?) then (是)
  :考虑网络通信和远程控制设备;
else (否)
  :不考虑远程控制;
endif
repeat
:设计控制系统架构和连接方式;
:编写控制系统程序;
:进行仿真测试和调试;
if (测试通过?) then (是)
  :安装控制系统;
  :调试和优化控制程序;
else (否)
  :调整程序并重新测试;
endif
repeat while(最终测试通过?) is(否) not (是)
:培训操作人员和维护人员;
:记录控制系统细节和参数;
:最终验收控制系统;
:完善文档和记录;
stop
@enduml

@startuml
start
:接收设备;
repeat
  :查看设备型号;
  if (设备可修复?) then (是)
    :记录信息;
    :问题诊断;
    :报价;
    if (客户同意报价?) then (是)
      :报价确认;
      :签署维修协议;
      break
    else (否)
      :结束维修流程;
      stop
    endif
  else (否)
    :不修复;
    :结束维修流程;
    stop
  endif
repeat while (设备未修复) is(重试) not(继续)
stop
@enduml

@startuml
start

:接收平板设备;
repeat
  :查看屏幕是否坏;
  if (屏幕坏了?) then (是)
    :确定内屏或外屏坏;
    if (内屏坏了?) then (是)
      :记录信息;
      :维修或更换内屏;
    else (否)
      :记录信息;
      :维修或更换外屏;
    endif
  else (否)
    :屏幕正常;
  endif
  :查看按键;
  if (按键有问题?) then (是)
    :维修按键;
  else (否)
    :按键正常;
  endif
  :查看电池;
  if (电池需要更换?) then (是)
    :更换电池;
  else (否)
    :电池正常;
  endif
  :清洁设备;
  :设备测试;
  if (测试通过?) then (是)
    :客户验收;
    :交付设备;
    stop
  else (否)
    :问题调试;
    if (问题解决?) then (是)
      :重新测试;
      if (测试通过?) then (是)
        :客户验收;
        :交付设备;
        stop
      else (否)
        :问题调试;
      endif
    else (否)
      :结束维修流程;
      stop
    endif
  endif
repeat while(问题未解决?) is(继续维修) not (停止)
@enduml

@startuml
start

:接收相机设备;
:记录设备信息;
:检查镜头;
repeat
  if (镜头有问题?) then (是)
    :维修或更换镜头;
  else (否)
    :镜头正常;
  endif
  :检查相机盖;
  if (相机盖有问题?) then (是)
    :维修或更换相机盖;
  else (否)
    :相机盖正常;
  endif
  :检查按钮;
  if (按钮有问题?) then (是)
    :维修按钮;
  else (否)
    :按钮正常;
  endif
  :检查显示屏;
  if (显示屏有问题?) then (是)
    :维修或更换显示屏;
  else (否)
    :显示屏正常;
  endif
  :检查存储卡;
  if (存储卡有问题?) then (是)
    :处理存储卡问题;
  else (否)
    :存储卡正常;
  endif
  :设备测试;
  if (测试通过?) then (是)
    :客户验收;
    :交付设备;
    stop
  else (否)
    :问题调试;
  endif
repeat while(交付测试通过?) is(不通过) not (通过)

stop
@enduml

@startuml
start
:接收AirPods设备;
:记录设备信息;
repeat
  :检查充电盒;
  if(无法修复) then(否)
  else(是)
    :结束维修;
    stop
  endif
  :检查左右耳机;
  if(无法修复) then(否)
  else(是)
    :结束维修;
    stop
  endif
  :检查耳机外壳;
  if(无法修复) then(否)
  else(是)
    :结束维修;
    stop
  endif
  :检查耳机内部电池;
  if(无法修复) then(否)
  else(是)
    :结束维修;
    stop
  endif
