flowchart:
@startuml
start
:获取购物车信息并计算好优惠;

:从ums_member_receive_address表中获取会员收货地址列表;
:获取该会员所有优惠券信息;

repeat :根据use_type判断每个优惠券是否可用;

if (全场通用?) then (是)
  if (判断所有商品总金额是否满足使用起点金额?) then (是)
    :得到用户可用优惠券列表;
  else (否)
    :得到用户不可用优惠券列表;
    stop
  endif
else (否)
  if (指定分类?) then (是)
    if (判断指定分类商品总金额是否满足使用起点金额?) then (是)
      :得到用户可用优惠券列表;
    else (否)
      :得到用户不可用优惠券列表;
      stop
    endif
  else (否)
    if (指定商品?) then (是)
      if (判断指定商品总金额是否满足使用起点金额?) then (是)
        :得到用户可用优惠券列表;
      else (否)
        :得到用户不可用优惠券列表;
        stop
      endif
    else (否)
      :无可用优惠券;
      stop
    endif
  endif
endif
repeat while(检查所有优惠券是否处理完成?) is(未处理完成) not(已处理完成)

:得到用户可用优惠券列表;
:获取用户积分;
:获取积分使用规则;

:计算总金额、活动优惠、应付金额;
stop
@enduml

@startuml
start
:客户申请退货;
repeat
  :客服确认原因;
  if (责任方?) then (卖家责任)
    :登录我的淘宝点击退货;
    :联系快递取件
或自行寄回商品;
    if (商品是否无损?) then (是)
      :退货;
      :退款;
    else (否)
      :不受理退换货;
    endif
  else (非卖家责任)
    if (责任方?) then (买家责任)
      if (符合退换货资格?) then (是)
        :寄回商品;
      else (否)
        :不受理退换货;
      endif
    else (配货责任)
      :拒绝签收;
      :联系客服;
    endif
  endif
repeat while(与客服成功协商?) is(未协商成功) not(已协商成功)
stop
@enduml

@startuml
|APP客户端|
start
:展示自建入口;
:点击进入;
|H5前端|
repeat
:填写表单创建圈子;
repeat while(表单信息是否完整?) is(表单信息不完整) not(表单信息完整)
|Server API|
:提交表单，等待审核;
|CRM后台|
if (是否符合自建圈子要求?) then (是)
  :通过;
  |Server API|
  :私信通知通过;
else (否)
  |APP客户端|
  :展示结果;
  stop
endif

|CRM后台|
if (审核是否通过?) then (是)
  |Server API|
  :私信通知通过;
else (否)
  |Server API|
  :私信通知不通过;
  stop
endif

|APP客户端|
:展示创建圈任务入口;
|H5前端|
:展示具体建圈任务;
|Server API|
if (是否完成建圈任务?) then (是)
  :私信通知建圈任务成功;
else (否)
  :私信通知建圈任务失败;
endif

|APP客户端|
:保存自建圈子;
:展示结果;
stop
@enduml

@startuml
start
:身份验证;
while(身份验证)is(错误)
endwhile

if (身份是培训人员?) then (是)
  :显示已有课程项目;
  repeat
    :选择上传项目或选择已有项目进行删除;
    if (确认更新?) then (是)
      :完成更新;
    else (取消更新或无更新)
    endif
  repeat while(确认操作?) is(需要继续) not(操作完成)
  :通知反馈消息;
else (否)
  :显示可下载课程项目;
  :选择下载项目，选择是否下载;

  if (确认下载?) then (是)
    :完成下载项目;
  else (取消下载或下载内容为空)
  endif
  :通知反馈消息;
endif

stop
@enduml

@startuml
start
:创建空订单包列表;

if(订单列表只有一个订单)then(是)
    :创建一个新的订单包并添加这个订单到订单包中;
    :添加到总订单包列表中;
else(否)
    :计算所有订单之间的最大距离;

    repeat
    :初始化布尔数组;
    while(遍历所有订单)is(foreach order)
        if(订单未分配到订单包)then(是)
            :创建新的订单包;
            :将当前订单添加到新的订单包中;
            :标记当前订单为已分配;

            while(检查其他订单)is(foreach other order)
                if(订单满足相邻或同小区条件)then(是)
                    :添加到当前订单包中;
                    :标记为已分配;
                endif
            endwhile
        :添加当前订单包到总订单包列表中;
        endif
    endwhile
    repeat while(尚有未分配的订单?) is(需要重新检查) not(所有订单已分配)
    :对每个订单包内部的订单进行排序;
    :对总的订单表列表进行排序;

endif

:输出所有的零担包机器包含的订单;
stop
@enduml

@startuml
start
:创建请购单;
repeat
  :经理审批;
  if (审批通过?) then (是)
    if (金额超过10000?) then (是)
      while (副总裁审批通过?) is (否)
        :副总裁审批;
      endwhile (是)
    endif
    :财务审核;
    :开具发票;
  else (否)
    stop
  endif
repeat while(经理仍需审批) is(未审批) not (审批完成)
stop
@enduml

@startuml
start
:客户提交工单;
:工单已创建并分配;
:根据优先级评估工单;
if (是否需要调查?) then (是)
  :远程支持;
  if (是否需要修复?) then (是)
    repeat :工单发送给工程团队;
    if (修复完成?) then (否)
    endif
  repeat while(修复完成?) is(否) not(是)
  endif
endif
:支持团队解决工单;
:工单关闭;
stop
@enduml

@startuml
start
repeat
  :查询;
  :检查可用性;
  if (是否可用?) then (可用)
    :验证有效性;
    if (是否有效?) then (有效)
      :发放图书;
      :更新图书状态;
    else (无效)
      :不发放图书;
    endif
  else (不可用)
  endif
repeat while(所有步骤成功处理完成?) is(未完成) not(已完成)
stop
@enduml

@startuml
start
if (是否已经是会员?) then (否)
  :填写注册表单;
  if (是否要注册?) then (是)
    if (通过 Facebook 登录?) then (是)
      :Facebook 认证;
    else (否)
      if (通过 Google 登录?) then (是)
        :Google 认证;
      else (否)
        :退出;
        stop
      endif
    endif
  else (否)
    :退出;
    stop
  endif
else (是)
  repeat
    :输入邮箱和密码;
    :验证凭据;
    if (登录失败?) then (是)
      if (忘记密码?) then (是)
        :重置密码;
      else (否)
      endif
    endif
  repeat while(是否需要再次尝试?) is(再次尝试) not(登录成功)
endif
:用户成功登录;
stop
@enduml

@startuml
start
:请求创建货运;
:从数据库中搜索客户;
if (客户是否存在?) then (是)
  :显示客户详情;
else (否)
  :显示错误信息;
  stop
endif

repeat  :创建货运;
  if (创建是否成功?) then (是)
    :保存到数据库;
    :打印发票;
  else (否)
    :拒绝创建消息;
    if (是否取消创建货运?) then (是)
      stop
    endif
  endif
repeat while(需要再次创建货运?) is(是) not (否)
stop
@enduml

@startuml
start
:初始化联系;
:初步机会工作;
if (是否加入其他供应商或更改需求?) then (是)
  :搜索替代方案;
  stop
else (否)
  :机会被接受;
  :创建提案项目计划;
  fork
    :分析并最终确定提案;
  fork again
    :创建交付项目计划;
  endfork
  :汇编附加信息;
repeat  :准备报价;
  repeat while(获得客户反馈?) is(有反馈) not(无反馈)
endif
stop
@enduml

@startuml
start
repeat
:列出产品;
:添加到购物车;
if (是否已添加到购物车?) then (是)
  :已添加到购物车;
  :登录;
  :查看购物车;
  fork
    :查看商店信息;
  fork again
    :打开链接;
  endfork
else (否)
  :用户已通知;
endif
repeat while(是否要继续添加产品?) is(要继续) not(已完成)
stop
@enduml

@startuml
start
:插入ATM卡;
:验证ATM卡;
if (卡是否有效?) then (有效)
  :输入密码;
  :授权密码;
  if (密码是否有效?) then (有效)
    repeat
    :输入取款金额;
    :检查余额;
    if (余额是否足够?) then (是)
      :从账户扣款;
      :从取款口取钱;
    else (否)
      :显示余额不足;
    endif
    repeat while(输入金额正确?) is(不正确) not (正确)
  else (无效)
    :弹出卡片;
    stop
  endif
else (无效)
  :弹出卡片;
  stop
endif
:显示余额;
:弹出卡片;
:取走卡片;
stop
@enduml

@startuml
start
:打开文字处理软件;
:创建文件;
:保存文件;
repeat :输入文档内容;
if (是否需要图形?) then (需要)
  :打开并使用图形软件;
else (不需要)
endif
if (是否需要表格?) then (需要)
  :打开并使用电子表格软件;
else (不需要)
endif
repeat while(文档内容是否完整?) is(不完整) not(完整)
:保存文件;
:打印硬拷贝;
:退出办公套件;
stop
@enduml

@startuml
start
repeat
    :类别选择;
    :头脑写作;
    :聚类和头脑风暴;
    if (项目太多?) then (是)
        :优先级排序和筛选;
    else (否)
    endif
    :讨论;
repeat while(讨论时间够用吗?) is(时间不够) not(时间充足)

if (项目太多?) then (是)
    :优先级排序和筛选;
endif

:优先级排序和筛选;
:决策;
stop
@enduml

@startuml
start
repeat
    :展示游戏;
    :实施游戏;
    :评估游戏;
repeat while(第一轮是否还有其他游戏?)
repeat
    :实施游戏;
    :重新评估游戏;
repeat while(没有游戏需要再次实施)
repeat
    :讨论结果;
    :决定是否需要再次实施游戏;
repeat while(所有游戏完成后讨论是否需要再次实施) is(需要再次实施) not(无需再次实施)
stop
@enduml

@startuml
start
:开始;
repeat :确定密钥值 (Ko);
if (块大小是否小于密钥长度?) then (是)
  :外部填充 (Ko xor 外部填充);
else (否)
  :内部填充 (Ko xor 内部填充);
  :计算哈希 (Hash(Ko xor 内部填充 || 文本));
endif
repeat while(块大小是否足够?) is(不足) not(足够)
:计算消息认证码;
stop
@enduml

@startuml
start
:图书查询;
:检查图书可用性;
if (图书可用吗?) then (不可用)
  :图书不可用;
  stop
else (可用)
  repeat
    :验证会员;
    if (会员有效吗?) then (无效)
      :注册会员;
    else (有效)
    endif
  repeat while(会员验证通过并可借阅?) is(验证未通过或无法借阅) not(验证通过并成功借阅)
  :检查会员已借书籍数量;
  if (超过最大限额?) then (是)
    :图书未借出;
  else (否)
    :借出图书;
    :添加会员、书籍及借书明细;
  endif
  :更新图书状态;
endif
stop
@enduml

@startuml
start
:灯不亮;
if (灯插好了吗?) then (否)
  :插好灯;
  stop
else (是)
  if (灯泡坏了吗?) then (是)
    :换灯泡;
    stop
  else (否)
    repeat
    :修理灯具;
    repeat while(灯是否亮了?) is(未亮) not(已亮)
  endif
endif
stop
@enduml

@startuml
start
while(购物完成)is(否)
repeat:
  :扫描商品;
repeat while(扫描成功?)
:将扫描信息发送至数据库;
:从数据库获取商品相关信息;
repeat  
  if (是否添加该商品?) then (是)
    :将商品添加至购物清单;
  else (否)
    :从购物清单中移除商品;
  endif
repeat while(商品库存充足?) is (库存不足) not(库存充足)
endwhile
:将购物信息发送至数据库;
if (支付方式是线上还是线下?) then (线上)
  :重定向到银行网页或其他在线支付系统;
  :支付账单;
else (线下)
  :数据发送到服务器;
  :在柜台支付账单;
endif
stop
@enduml

@startuml
start
:点击发布职位空缺;
repeat :系统呈现预职位空缺订单表单;
:选择'一般雇主'作为雇主类型;
:选择同意接受个人数据用途声明;
:点击下一步;
if (是否有未填写的字段?) then (是)
  :系统高亮显示未填写的字段;
  :系统将焦点定位到第一个未填写的字段;
else (否)
  :系统呈现职位空缺订单表单;
  :填写表单字段;
  :选择接受声明;
  :点击下一步;
  :系统呈现职位空缺摘要;
endif
repeat while(所有字段已填写?) is(不是) not(是) 
stop
@enduml

@startuml
start
:撰写文章;
:发布[草稿];
repeat  :审核草稿;
while(需要修改?)is(是)
  :进行修改;
  :发布[草稿];
  :审核草稿;
endwhile
:发布[修改后的草稿];
:审核最终草稿;
while(需要进一步修改?)is(是)
  :进行修改;
  :发布[修改后的草稿];
  :审核最终草稿;
endwhile
repeat while(再次审核?)is(是) not(无需审核)
:发布文章;
stop
@enduml

@startuml
start
:启动CASE工具;
:选择UML活动图用于生成流程;
:从EM中识别初始元素;
:为选择的UML图选择元素类型;
if (是否有选定的元素类型?) then (Yes)
  repeat
  :从EM中选择相关元素;
  :将EM元素生成到活动图元素;
  if (这是最后一个相关元素吗?) then (Yes)
    if (是否生成的最后一个元素?) then (Yes)
      :是否生成另一个UML图?;
    endif
  endif
  repeat while(是否生成的最后一个元素?) is(未生成) not(已生成)
else (No)
  :返回选择元素类型步骤;
endif
stop
@enduml

@startuml
start
:输入用户名和密码;
repeat
  :获取详细信息;
  :提交;
  while(验证通过?)is(否)
    :拒绝;
    :发生错误;
    repeat while(重新获取详细信息?) is(是) not (否)
  endwhile
:接受;
stop
@enduml

@startuml
start
:用户导航到登录页面;
repeat
  :输入用户名和密码;
  :提交用户凭证;
  if (凭证有效?) then (是)
    :重定向到仪表盘;
  else (否)
    :显示错误信息;
  endif
repeat while(尝试次数超过限制?) is(未超过) not(超过)
stop
@enduml

@startuml
start

:员工填写请假条;
:员工提交请假条;

repeat
  if (部门领导审批) then (批准)
    :员工得知通过;
  else (拒绝)
    :员工放弃申请;
  endif
repeat while(员工愿意继续申请?) is(继续) not(放弃)

stop
@enduml

@startuml
start

:进入微信充值;
:输入手机号码;
:选择话费套餐;
:完成支付;
repeat :输入支付密码;

if (确认支付) then (成功)
  :获取成功结果;
else (失败)
  :获取失败结果;
endif
repeat while(确认支付) is(失败) not(成功)

stop
@enduml

@startuml
start

:客户已注册;

:客户输入登录名和密码;
repeat
  if (登录名和密码正确？) then (是)
    break
  else (否)
    :登录名或密码无效;
    :客户重新输入登录信息;
  endif
repeat while (登录名和密码正确？) is (否) not (是)
endif
:客户成功登录到系统;

stop
@enduml

@startuml
start

:数据收集;
repeat
  :对账;
  :会计分录和调整;
  :审核财务报表;

  if (财务报表准确吗？) then (是)
    :最终报告;
  else (否)
      :调查并解决差异;
  endif
repeat while (数据收集完整?) is (未完成) not (完成)
:最终报告;
:内部审核和批准;
:期末结账;

stop
@enduml

@startuml
start

:客户提交工单;
repeat  :创建并分配工单;
:根据优先级审核工单;
if (需要调查？) then (是)
  :远程;
  if (需要修复) then (是)
    :工单发送至工程团队;
  else (否)
  endif
else (否)
endif
:支持团队解决工单;
:关闭工单;
repeat while(客户提供更多信息?) is(未提供足够信息) not(所有信息已提供)
stop
@enduml

@startuml
start

:头脑风暴;
:新产品;
repeat
if (原型制作？) then (是)
  break
else (否)
    :评审;
    :完善;
endif
repeat while (快速设计)
:设计;
repeat
:实施;
:测试;
repeat while(需要改善吗?) is(是) not(否)

stop
@enduml

@startuml
start

:开始;
:闹钟响起;
repeat
if (准备好面对世界了吗？) then (是)
  break
else (否)
    :按下贪睡按钮;
    :再拖延5分钟;
endif
repeat while (感到足够清醒去起床?) is (不够清醒) not (够清醒)
:起床;
:结束;
stop
@enduml

@startuml
start

:开始;
:登录;
repeat
  :搜索;
  if (检查是否有可用房间) then (是)
    break
  else (否)
